    color = ctx.color || 'currentColor',
    title,
    desc,
    class: className,
    ariaLabel,
    ...restProps
  }: Props = $props();

  let ariaDescribedby = `${title?.id || ''} ${desc?.id || ''}`.trim();
  const hasDescription = !!(title?.id || desc?.id);
  const isLabeled = !!ariaLabel || hasDescription;
